import React, {useState} from 'react';
import styles from "./MainContent.module.css"
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import {useDispatch} from "react-redux";



const MainContent = () => {
    const dispatch = useDispatch()
    const [value, setValue] = useState(new Date())
    const [costs, setCosts] = useState({amount: "", category: ""})
    console.log(value.getMonth())

    const costsHandler = (event)=>{
        setCosts({...costs, [event.target.name]: event.target.value})
    }
    const createEntry = ()=>{
        dispatch()
    }

    return (
        <div className={styles.wrapper}>
            <div className={styles.calendar}>
                <Calendar
                    onChange={setValue}
                    value={value}
                />
            </div>
            <div className={styles.inputField}>
                <input name="amount" value={costs.amount} type="number" onChange={costsHandler} placeholder="Spent Amount ($)" />
                <input name = "category" value={costs.category} type="text" onChange={costsHandler} placeholder="Category" />
                <button onClick={createEntry}>Create Entry</button>
            </div>
        </div>
    );
};

export default MainContent;
