import styles from "./RegLog.module.css"
import {useState} from "react";
import {Link} from "react-router-dom";
import {useDispatch} from "react-redux";
import {registerAction} from "../../../redux/actions/actionCreator";
import axios from "axios";


function Register() {
    const [form, setForm] = useState({email: "", username: "", password: ""})
    const updateForm = (event) =>{
        setForm({...form, [event.target.name]: event.target.value})
    }
    const dispatch = useDispatch()
    const reg = () => {
        axios({
            method: 'post',
            url: 'api/auth/register',
            data: form
        });
    }

    return (
        <div className={styles.wrapper}>
            <div className="container">
                <h1>Register</h1>
                <p>Please fill in this form to create an account.</p>
                <div>
                    <label htmlFor="email"><b>Username</b></label>
                    <input type="text" placeholder="Enter Username" name="username" id="username" required onChange={updateForm}/>

                    <label htmlFor="email"><b>Email</b></label>
                    <input type="text" placeholder="Enter Email" name="email" id="email" required onChange={updateForm}/>

                    <label htmlFor="psw"><b>Password</b></label>
                    <input type="password" placeholder="Enter Password" name="psw" id="psw" required onChange={updateForm}/>

                    <div>
                        <button type="submit" onClick={reg} className={styles.registerbtn}>Register</button>
                    </div>

                </div>
            </div>


            <div className={`{styles.container} {styles.signin}`}>
                <p>Already have an account? <Link to="/login">Sign in</Link>.</p>
            </div>
        </div>
    )
}

export default Register
