import React, {useRef} from 'react';
import styles from  "./Modal.module.css"

const Modal = ({active, setActive, children}) => {
    const modW = useRef()
    const closeM = (e)=>{
        if(e != modW) setActive(false)
    }
    return (
        <div>
            <div className= {active ? `${styles.modal} ${styles.active}`: `${styles.modal}`} onClick={closeM}>
                <div ref={modW} className={active ? `${styles.modal_content} ${styles.active}`: `${styles.modal_content}`}>
                    {children}
                </div>
            </div>
        </div>
    );
};

export default Modal;
