import styles from "./RegLog.module.css";
import React, {useContext, useState} from "react"
import {Link} from "react-router-dom";
import axios from "axios";
import {useDispatch} from "react-redux";
import {authLogInAction} from "../../../redux/actions/actionCreator";
import {AuthContext} from "../../../context/AuthContext";



function Login(props){
    const auth = useContext(AuthContext)
    const dispatch = useDispatch()
    const [form, setForm] = useState({email: "", password: ""})
    const updateForm = (event) =>{
        setForm({...form, [event.target.name]: event.target.value})
    }

    const log = async () => {
        try {
            const body = JSON.stringify(form)
            const res = await axios.post("/api/auth/login", body, {
                headers:{
                    "Content-Type": "application/json"
                }
            })
            console.log(res)
            auth.login(res.token, res.userID)
        }catch (e){

        }

    }
    return (
        <div className={styles.wrapper}>
            <div className="container">
                <h1>Login</h1>
                <p>Please fill in this form to Log In.</p>
                <div>

                    <label htmlFor="email"><b>Email</b></label>
                    <input type="text" placeholder="Enter Email" name="email" id="email" required onChange={updateForm}/>

                    <label htmlFor="password"><b>Password</b></label>
                    <input type="password" placeholder="Enter Password" name="password" id="password" required onChange={updateForm}/>

                    <div>

                        <button type="submit"  onClick={log} className={styles.loginbtn}>Log In</button>
                    </div>
                    <Link to="/register">
                        <button type="submit" className={styles.registerbtn}>Register</button>
                    </Link>

                </div>
            </div>

        </div>
    )
}

export default Login
