import styles from  "./Modal.module.css"
import {useRef} from "react";

const Modal = ({active, setActive, children}) => {
    const clM =useRef()
    const modM = useRef()
    const closeM = (e)=>{
       // if(e.target.ref !== modM){setActive(false)}
        console.log(e.target.className !== "")
    }
    return (
        <div>
            <div className= {active ? `${styles.modal} ${styles.active}`: `${styles.modal}`} ref={clM} onClick={closeM}>
                <div ref={modM} className={active ? `${styles.modal_content} ${styles.active}`: `${styles.modal_content}`}>
                    {children}
                </div>
            </div>
        </div>
    );
};

export default Modal;
