import {Constants} from "../constants/authConstance"
import axios from "axios";
export const loadNotes = (info) => async (dispatch)=>{
    try {
        const {data} = await axios.get("/api/note/load", {headers: {Authorization : `Bearer ${info.token}`}})
        dispatch({type: Constants.LOAD_NOTES, payload: data})
    }catch (e) {
        console.log(e.message)
    }
}

export const createNote = (info) => async (dispatch)=>{
    try {
        const {data} = await axios.post("/api/note/create", info, {headers: {Authorization : `Bearer ${info.token}`}})
        dispatch({type: Constants.CREATE_NOTE, payload: data.note})
        window.M.toast({html: "Note successfully created", classes: "rounded"})
    }catch (e) {
        window.M.toast({html: "Note`s name must contain at least 4 characters or note already exist", classes: "rounded"})
    }
}

export const deleteNote = (info) => async (dispatch)=>{
    try {
        await axios.delete("/api/note/delete", info)
        dispatch({type: Constants.CREATE_NOTE, payload: data.note})
        window.M.toast({html: "Note successfully created", classes: "rounded"})
    }catch (e) {
        window.M.toast({html: "Note`s name must contain at least 4 characters or note already exist", classes: "rounded"})
    }
}
