import React, {useContext} from 'react';
import styles from "./Note.module.css"
import {useDispatch} from "react-redux";
import {deleteNote} from "../../../../../redux/actions/actionCreator";
import {AuthContext} from "../../../../../context/AuthContext";

const Note = ({note, counter}) => {
    const {token} = useContext(AuthContext)
    const dispatch = useDispatch()
    const isPrivate = ()=>{
        if(note.prv){return "private"}
        else return "public"
    }

    return (
        <div className={styles.wrapper}>
            <div className={styles.name_counter}>
                <div>{counter}</div>
                <div>{note.name}</div>
                <div>{isPrivate()}</div>
                <div>Words: 1000</div>
            </div>

            <button className={styles.delete_btn} onClick={()=>dispatch(deleteNote({_id: note._id, token}))}>Remove</button>
        </div>
    )
};

export default Note;
