import React, {useRef} from 'react';
import styles from "./SideBar.module.css"
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faCogs} from "@fortawesome/free-solid-svg-icons/faCogs";
import {faPlus} from "@fortawesome/free-solid-svg-icons";
import M from "materialize-css"


const SideBar = () => {
    const Ref = useRef();
    const instances = M.Modal.init(Ref);
    const modal_show = () => {
        instances.open();
    }
    return (
        <div className={styles.wrapper}>
            <ul>
                <li><FontAwesomeIcon onClick={modal_show} icon={faPlus} size="2x"/></li>
                <li><FontAwesomeIcon icon={faCogs} size="2x"/></li>
                <li><FontAwesomeIcon icon={faCogs} size="2x"/></li>
                <li><FontAwesomeIcon icon={faCogs} size="2x"/></li>
            </ul>


            <a className="waves-effect waves-light btn modal-trigger" onClick={modal_show}>Modal</a>


            <div id="modal1" ref={Ref} className="modal">
                <div className="modal-content">
                    <h4>Modal Header</h4>
                    <p>A bunch of text</p>
                </div>
                <div className="modal-footer">
                    <a href="#" className="modal-close waves-effect waves-green btn-flat">Agree</a>
                </div>
            </div>
        </div>

    );
};


export default SideBar;
