import {Constants} from "../constants/authConstance"
import axios from "axios";
import {useAuth} from "../../hooks/authHook";
export const loadNotes = () => async (dispatch)=>{
    try {
        const {data} = await axios.get("/api/note/load")
        dispatch({type: Constants.LOAD_NOTES, payload: data})
    }catch (e) {
        console.log(e.message)
    }
}

export const createNote = ({name, prt, token}) => async (dispatch)=>{
    try {
        const {data} = await axios.post("/api/note/create", {name, prt}, {headers: {Authorization : `Bearer ${token}`}})
        dispatch({type: Constants.CREATE_NOTE, payload: data})
    }catch (e) {
        console.log(e.message)
    }
}
