import React, {useState} from 'react';
import styles from "./AddWords.module.css"
import {translateText} from "../../../../../../translate";

const AddWords = () => {
    const [input_txt, setInputTXT] = useState("")
    const [output_txt, setOutputTXT] = useState("")

    function inHandler(e) {
        translateText(e.target.value, "uk")
            .then((res)=>{
                setOutputTXT(res)
                console.log(res)
            }).catch((err)=>{
                console.log(err)
        })
        setInputTXT(e.target.value)
    }

    function outHandler(e) {
        setOutputTXT(e.target.value)
    }


    return (
        <div className={styles.wrapper}>
           <div >
               <input className={styles.input} type="text" value={input_txt} onChange={inHandler} maxLength="20" id="input_text" placeholder="ENG"/>
           </div>
            <div>
                <input className={styles.output} type="text" value={output_txt} onChange={outHandler} maxLength="20" id="output_text" placeholder="TRANSLATE"/>
            </div>
            <div>
                <button className={styles.add_btn}>ADD</button>
            </div>
        </div>
    );
};

export default AddWords;
