import styles from "./RegLog.module.css"
import {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import axios from "axios";


function Register(props) {
    const [form, setForm] = useState({email: "", username: "", password: ""})
    const updateForm = (event) => {
        setForm({...form, [event.target.name]: event.target.value})
    }
    const reg = async () => {
        setForm({...form, password: ""})
        try {
            const body = JSON.stringify(form)
            const res = await axios.post("/api/auth/register", body, {
                headers: {
                    "Content-Type": "application/json"
                }
            })
            window.M.toast({html: "User Successfully created"})
            setForm({username: "",email: "", password: ""})
            setTimeout(()=>{
                window.location.assign('/login')
            }, 1500)
        }catch (e){
            window.M.toast({html: "Invalid data"})
        }



    }

    return (
        <div className={styles.wrapper}>
            <div className="container">
                <h1>Register</h1>
                <p>Please fill in this form to create an account.</p>
                <div>
                    <label htmlFor="email"><b>Username</b></label>
                    <input type="text" value={form.username} placeholder="Enter Username" name="username" id="username" required
                           onChange={updateForm}/>

                    <label htmlFor="email"><b>Email</b></label>
                    <input type="text" value={form.email} placeholder="Enter Email" name="email" id="email" required
                           onChange={updateForm}/>

                    <label htmlFor="psw"><b>Password</b></label>
                    <input type="password" value={form.password} placeholder="Enter Password" name="password" id="password" required
                           onChange={updateForm}/>

                    <div>
                        <button type="submit" onClick={reg} className={styles.registerbtn}>Register</button>
                    </div>

                </div>
            </div>


            <div className={`{styles.container} {styles.signin}`}>
                <p>Already have an account? <Link to="/login">Sign in</Link>.</p>
            </div>
        </div>
    )
}

export default Register
