import styles from  "./Modal.module.css"
import {useRef} from "react";

const Modal = ({active, setActive, children}) => {

    const closeM = (e)=>{
        if(e.target.className === `${styles.modal} ${styles.active}`){setActive(false)}
        console.log(e.target.className)
        console.log(styles.modal)
    }
    return (
        <div>
            <div className= {active ? `${styles.modal} ${styles.active}`: `${styles.modal}`}  onClick={closeM}>
                <div  className={active ? `${styles.modal_content} ${styles.active}`: `${styles.modal_content}`}>
                    {children}
                </div>
            </div>
        </div>
    );
};

export default Modal;
