import React, {useState} from 'react';
import styles from "./CreateNote.module.css"
import {useDispatch} from "react-redux";
import {createNote} from "../../../../../redux/actions/actionCreator";
import {useAuth} from "../../../../../hooks/authHook";


const CreateNote = ({setActive}) => {
    const {token} = useAuth()
    const dispatch = useDispatch()
    const [name, setName] = useState("")
    const [prv, setPrv] = useState(false)

    const changeName = (e)=>{
        setName(e.target.value)
    }

    const changePrt = (e)=>{
        setPrv(e.target.checked)
    }
    const createN = (e)=>{
        e.preventDefault()
        dispatch(createNote({name: name, prv: prv, token: token}))
        setActive(false)
        setName("")
    }


    return (
        <div className={styles.wr}>
            <div className={styles.wrapper}>
                <label ><b>Name</b></label>
                <input type="text" onChange={changeName} value={name} placeholder="Enter name" name="name" required/>
                    <p>
                        <label>
                            <input onClick={changePrt} type="checkbox" className="filled-in"/>
                            <span>Private</span>
                        </label>
                    </p>
                <div className={styles.btns}>
                    <button className={styles.create_btn} onClick={createN} type="submit">Create</button>
                    <button className={styles.close_btn} type="button" onClick={()=>{setActive(false)}}>Cancel</button>
                </div>
            </div>

        </div>
    )
}

export default CreateNote
