import React, {useContext, useEffect, useState} from 'react';
import styles from "./MainContent.module.css"
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import {useDispatch} from "react-redux";
import {createNote, loadNotes} from "../../../../redux/actions/actionCreator";
import {AuthContext} from "../../../../context/AuthContext";




const MainContent = () => {
    const {token, userID} = useContext(AuthContext)
    const dispatch = useDispatch()
    const [value, setValue] = useState(new Date())
    const [costs, setCosts] = useState({amount: "", category: ""})

    const costsHandler = (event)=>{
        setCosts({...costs, [event.target.name]: event.target.value})
    }

    useEffect(()=>{
        dispatch(loadNotes({
            id: userID,
            year: value.getFullYear(),
            month: value.getMonth(),
            day: value.getDay(),
            token: token
        }),[dispatch])
    })

    const createEntry = ()=>{
        const info = {
            id: userID,
            year: value.getFullYear(),
            month: value.getMonth(),
            day: value.getDay(),
            costs: costs,
            token: token
        }
        dispatch(createNote(info))
    }

    return (
        <div className={styles.wrapper}>
            <div className={styles.calendar}>
                <Calendar
                    onChange={setValue}
                    value={value}
                />
            </div>
            <div className={styles.inputField}>
                <input name="amount" value={costs.amount} type="number" onChange={costsHandler} placeholder="Spent Amount ($)" />
                <input name = "category" value={costs.category} type="text" onChange={costsHandler} placeholder="Category" />
                <button onClick={createEntry}>Create Entry</button>
            </div>
        </div>
    );
};

export default MainContent;
