import React, {useContext, useEffect, useState} from 'react';
import styles from "./MainContent.module.css"
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import {useDispatch, useSelector} from "react-redux";
import {createNote, loadNotes} from "../../../../redux/actions/actionCreator";
import {AuthContext} from "../../../../context/AuthContext";




const MainContent = () => {
    const n = useSelector(state => state.NotesReducer)
    const {token, userID} = useContext(AuthContext)
    const dispatch = useDispatch()
    const [value, setValue] = useState(new Date())
    const [costs, setCosts] = useState({amount: "", category: ""})

    const costsHandler = (event)=>{
        setCosts({...costs, [event.target.name]: event.target.value})
    }
    useEffect(()=>{
        const info = {
            id: userID,
            year: value.getFullYear(),
            month: value.getMonth(),
            day: value.getDate(),
            token: token
        }
        dispatch(loadNotes(info))
    })

    const createEntry = ()=>{
        const info = {
            id: userID,
            year: value.getFullYear(),
            month: value.getMonth(),
            day: value.getDate(),
            costs: costs,
            token: token
        }
        dispatch(createNote(info))
        setCosts({amount: "", category: ""})
    }

    return (
        <div className={styles.wrapper}>
        <div >
            <div>
                <Calendar
                    onChange={setValue}
                    value={value}
                />
            </div>
            <div className={styles.inputField}>
                <input name="amount" value={costs.amount} type="number" onChange={costsHandler} placeholder="Spent Amount ($)" />
                <input name = "category" value={costs.category} type="text" onChange={costsHandler} placeholder="Category" />
                <button onClick={createEntry}>Create Entry</button>
            </div>
        </div>
            <div>
                {n.map((e)=>{
                    if(e.year === value.getFullYear() && e.month === value.getMonth() && e.day === value.getDate()){
                        return <div>
                            {e.year}
                        </div>
                    }
                })}
            </div>
        </div>

    );
};

export default MainContent;
