import React, {useState} from 'react';
import styles from "./MainContent.module.css"
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import st from "materialize-css/dist/css/materialize.min.css"


const MainContent = () => {
    const [value, setValue] = useState(new Date());
    const [costs, setCosts] = useState({amount: null, category: null})

    const costsHandler = (event)=>{
        setCosts({...costs, [event.target.name]: event.target.value})
    }
    return (
        <div className={styles.wrapper}>
            <div className={styles.calendar}>
                <Calendar
                    onChange={setValue}
                    value={value}
                />
            </div>
            <div className={styles.inputField}>
                <input name="amount" type="number" onChange={costsHandler} placeholder="Spent Amount ($)" />
                <input name = "category" type="text" onChange={costsHandler} placeholder="Category" />
                <button>Create Entry</button>
            </div>
        </div>
    );
};

export default MainContent;
